(ns advent-of-code-22.day6
  (:gen-class)
  (:require [clojure.java.io :as io])
  (:require [clojure.string :as str])
  (:require [clojure.set])
  (:require [clojure.pprint :as pp]))

(defn sol [line i n]
  (if (= (count (distinct (take n line))) n)
    (+ i (- n 1))
    (sol (drop 1 line) (+ i 1) n)))

(def input "")

(sol input 1 4)
(sol input 1 14)